mapper.select:
SELECT eha.sys_id, eha.order_no, eha.user_cate, eha.user_id, uc_ub_ur.full_name, uc_ub_ur.user_gender, uc_ub_ur.user_id_card_no, uc_ub_ur.user_cellphone_no, uc_ub_ur.relative_id, uc_ub_ur.relative_name, uc_ub_ur.relative_gender, uc_ub_ur.relative_id_card_no, uc_ub_ur.relative_cellphone_no, uc_ub_ur.card_cates, uc_ub_ur.card_no FROM entity_healthy_appointment eha LEFT JOIN entity_purchse_main epm ON epm.order_no = eha.order_no LEFT JOIN (SELECT ub.user_id, ub.full_name, ub.gender AS user_gender, ub.id_card_no AS user_id_card_no, ub.cellphone_no AS user_cellphone_no, ur.relative_id, ur.relative_name, ur.gender AS relative_gender, ur.id_card_no AS relative_id_card_no, ur.cellphone_no AS relative_cellphone_no, uc.card_cates, uc.card_no FROM user_cards uc LEFT JOIN user_basic ub ON ub.user_id = uc.user_id LEFT JOIN user_relatives ur ON ur.user_id = uc.user_id )uc_ub_ur ON uc_ub_ur.user_id = eha.user_id WHERE NOW() BETWEEN epm.creation_time AND DATE_ADD(epm.creation_time, INTERVAL 1 YEAR)
AND eha.user_id=#{userId}
AND eha.user_cate=#{userCate}
