mapper.select:
SELECT eham.sys_id sysId, epm.package_name packageName, eham.hr_rest_count hrRestCount, eham.hr_total_count hrTotalCount, epa.periods, ea.service_name serviceName FROM entity_hr_appointment_main eham LEFT JOIN exam_package_main epm ON epm.sys_id = eham.package_id LEFT JOIN exam_package_append epa ON epa.package_main_id = epm.sys_id LEFT JOIN exam_append ea ON ea.sys_id = epa.append_id WHERE 1 = 1 AND eham.is_deleted = 0
AND eham.entity_no = #{params.entityNo}
ORDER BY eham.creation_time DESC

SELECT eham.sys_id sysId, eham.entity_no entityNo, eham.service_option serviceOption, eham.appointment_date appointmentDate, eham.address, ( SELECT count(ehad.sys_id) FROM entity_hr_appointment_detail ehad WHERE 1 = 1
AND eham.sys_id = #{params.sysId}
) AS tjygrs, yyxm.name yyxmName, yyxm.countDetail FROM entity_hr_appointment_main eham LEFT JOIN ( SELECT eham1.sys_id eham1_sys_id, em.name name, count(ed.sys_id) countDetail FROM entity_hr_appointment_main eham1 LEFT JOIN exam_package_detail epd ON epd.exam_main_id = eham1.package_id LEFT JOIN exam_master em ON em.sys_id = epd.exam_master_id LEFT JOIN exam_detail ed ON ed.sys_id = epd.exam_detail_id GROUP BY epd.exam_master_id ) yyxm ON yyxm.eham1_sys_id = eham.sys_id WHERE 1 = 1
AND eham.sys_id = #{params.sysId}
