mapper.select:
SELECT eham.sys_id sysId, epm.package_name packageName, eham.hr_rest_count hrRestCount, eham.hr_total_count hrTotalCount, epa.periods, ea.service_name serviceName, ea.cates FROM entity_hr_appointment_main eham LEFT JOIN exam_package_main epm ON epm.sys_id = eham.package_id LEFT JOIN exam_package_append epa ON epa.package_main_id = epm.sys_id LEFT JOIN exam_append ea ON ea.sys_id = epa.append_id WHERE 1 = 1 AND eham.is_deleted = 0 AND eham.is_cancelled = 0 AND eham.status = 1
AND eham.entity_no = #{params.entityNo}
ORDER BY eham.creation_time DESC

SELECT eham.sys_id sysId, eham.entity_no entityNo, eham.service_option serviceOption, eham.appointment_date appointmentDate, eham.address, eham.hr_total_count hrTotalCount, ( SELECT count(ehad.sys_id) FROM entity_hr_appointment_detail ehad WHERE 1 = 1 AND ehad.is_transfered = 1
AND eham.sys_id = #{params.sysId}
) AS tjygrs, yyxm.name yyxmName, yyxm.countDetail FROM entity_hr_appointment_main eham LEFT JOIN ( SELECT eham1.sys_id eham1_sys_id, em.name name, count(ed.sys_id) countDetail FROM entity_hr_appointment_main eham1 LEFT JOIN exam_package_detail epd ON epd.exam_main_id = eham1.package_id LEFT JOIN exam_master em ON em.sys_id = epd.exam_master_id LEFT JOIN exam_detail ed ON ed.sys_id = epd.exam_detail_id GROUP BY epd.exam_master_id ) yyxm ON yyxm.eham1_sys_id = eham.sys_id WHERE 1 = 1
AND eham.sys_id = #{params.sysId}

SELECT eham.sys_id sysId, eham.entity_no entityNo, ub.user_id userId, ub.head_image headImage, ub.full_name fullName, ub.chinese_spell chineseSpell, ( SELECT COUNT(uem1.sys_id) FROM user_entity_mapping uem1 WHERE 1=1 AND uem1.is_active = 1
AND uem1.entity_id = #{params.entityId}
) AS countTotal, eham.package_id packageId FROM user_entity_mapping uem LEFT JOIN user_basic ub ON ub.user_id = uem.user_id LEFT JOIN entity_hr_appointment_main eham ON eham.entity_no = uem.entity_id WHERE 1 = 1
AND uem.entity_id = #{params.entityId}
AND eham.package_id = #{params.packageId}
GROUP BY uem.user_id

SELECT ehad.user_id userId FROM entity_hr_appointment_main eham LEFT JOIN entity_hr_appointment_detail ehad ON ehad.main_id = eham.sys_id WHERE 1 = 1 AND eham.is_deleted = 0 AND is_cancelled = 0 AND status = 1 AND ehad.user_id is not null
AND eham.entity_no = #{params.entityId}
AND eham.package_id = #{params.packageId}

SELECT oa.entity_id entityId, ub.user_id userId, ub.full_name fullName, ub.head_image headImage FROM order_appointment oa LEFT JOIN order_main om ON om.order_no = oa.order_no LEFT JOIN user_basic ub ON ub.user_id = oa.user_id WHERE 1 = 1 AND oa.is_cancelled = 0 AND om.is_deleted = 0 AND ub.isactive = 1 AND ub.is_hr = 0
AND oa.entity_id = #{eyHrAtMainScreenSetVoZ.entityId}
AND om.reference_no = #{eyHrAtMainScreenSetVoZ.referenceNo}
AND (oa.user_confirm = 0 OR oa.user_confirm = 1)
AND oa.user_confirm = 0
AND oa.user_confirm = 1
AND oa.appointment_cates = #{eyHrAtMainScreenSetVoZ.appointmentCates}
GROUP BY oa.user_id
