mapper.select:
SELECT erd.remark remark, erd.answer_detail answerDetail, eha.sys_id sysId, eha.submit_time submitTime, eha.risk_cate riskCate, eha.existed_issue existedIssue, eha.suggestions suggestions, eha.remarks remarks, ub.full_name fullName, se.entity_name relativeName, se.entity_id entityName, ub.gender gender FROM exam_user_healthy_appraisal eha LEFT JOIN exam_user_record_main erm ON eha.user_id = erm.user_id LEFT JOIN exam_user_record_detail erd ON erm.sys_id = erd.main_id
LEFT JOIN user_basic ub ON ub.user_id = eha.user_id LEFT JOIN user_entity_mapping uem ON uem.user_id = ub.user_id LEFT JOIN sys_entity se ON se.entity_id = uem.entity_id WHERE 1 = 1
AND eha.user_id = #{userId}
AND eha.sys_id = #{sysId}

SELECT erd.remark remark, erd.answer_detail answerDetail, eha.sys_id sysId, eha.submit_time submitTime, eha.risk_cate riskCate, eha.existed_issue existedIssue, eha.suggestions suggestions, eha.remarks remarks, ur.relative_name relativeName, se.entity_name entityName, se.entity_id, ur.gender gender FROM exam_user_healthy_appraisal eha LEFT JOIN exam_user_record_main erm ON eha.user_id = erm.user_id LEFT JOIN exam_user_record_detail erd ON erm.sys_id = erd.main_id
LEFT JOIN user_relatives ur ON ur.relative_id = eha.user_id LEFT JOIN user_entity_mapping uem ON uem.user_id = ur.user_id LEFT JOIN sys_entity se ON se.entity_id = uem.entity_id WHERE 1 = 1
AND eha.user_id = #{userId}
AND eha.sys_id = #{sysId}

SELECT eha.sys_id, eha.order_no, eha.user_cate, eha.user_id, eha.risk_cate, eha.existed_issue, eha.remarks, eha.suggestions, eha.is_submitted, eha.submit_time, eha.submit_by, ub.full_name FROM exam_user_healthy_appraisal eha LEFT JOIN user_basic ub ON ub.user_id = eha.submit_by WHERE 1 = 1
AND eha.order_no = #{examUserHealthyAppraisal.orderNo}
AND eha.user_id = #{examUserHealthyAppraisal.userId}
AND eha.sys_id = #{examUserHealthyAppraisal.sysId}
ORDER BY eha.submit_time DESC

SELECT eha.sys_id, eha.order_no, eha.user_cate, eha.user_id, eha.risk_cate, eha.existed_issue, eha.remarks, eha.suggestions, eha.is_submitted, eha.submit_time, eha.submit_by, ub.full_name FROM exam_user_healthy_appraisal eha LEFT JOIN user_basic ub ON ub.user_id = eha.submit_by WHERE 1 = 1
AND eha.order_no = #{examUserHealthyAppraisal.orderNo}
AND eha.user_id = #{examUserHealthyAppraisal.userId}
AND eha.submit_by = #{examUserHealthyAppraisal.submitBy}
ORDER BY eha.submit_time DESC LIMIT 0,1
