mapper.select:
select ufd.user_id, ub.head_image, ub.full_name,-- 姓名 ub.gender,-- 性别 TIMESTAMPDIFF(YEAR, ub.date_of_birth, CURDATE()) AS user_age, se.entity_name, ufd.effect_to, ub.is_hr, ufd.order_no, se.entity_id from user_family_doctor as ufd LEFT JOIN user_basic as ub ON ub.user_id=ufd.user_id LEFT JOIN user_entity_mapping as uem ON uem.user_id=ub.user_id LEFT JOIN sys_entity as se On se.entity_id=uem.entity_id where ufd.doctor_no=#{params.relatedNo} and ufd.status=#{params.status1}
and ub.full_name LIKE CONCAT('%', #{params.name}, '%')
UNION select eha.user_id, ub.head_image, ub.full_name,-- 姓名 ub.gender,-- 性别 TIMESTAMPDIFF(YEAR, ub.date_of_birth, CURDATE()) AS user_age, se.entity_name, eed.effect_to, ub.is_hr, eha.order_no, se.entity_id from entity_healthy_appointment as eha LEFT JOIN user_basic as ub ON ub.user_id=eha.user_id LEFT JOIN sys_entity as se ON se.entity_id=eha.entity_no LEFT JOIN entity_exclusive_doctor as eed ON eed.doctor_no=eha.related_no WHERE eha.related_no=#{params.relatedNo} and eha.status=#{params.status2}
and ub.full_name LIKE CONCAT('%', #{params.name}, '%')

SELECT

FROM
   order_appointment
WHERE 1 = 1

    AND order_no = #{orderAppointment.orderNo}


    AND appointment_cates = #{orderAppointment.appointmentCates}
