mapper.select:
SELECT om.sys_id sysId, om.order_no orderNo, om.order_time orderTime, ur.relative_name customerName, ub.full_name fullName,
epm.package_name packageName, oex.exam_master_item examMasterItem, om.order_cates appointmentCates, oai.option_1 option_1, om.parent_no parentNo, om.status, oai.amount
FROM order_main om LEFT JOIN order_additional_info oai ON oai.order_no = om.order_no LEFT JOIN order_customer oc ON oc.order_no = om.order_no LEFT JOIN user_relatives ur ON ur.relative_id = oc.relative_id LEFT JOIN user_basic ub ON ub.user_id = oc.relative_id LEFT JOIN exam_package_main epm ON epm.sys_id = om.reference_no LEFT JOIN order_extension_exam oex ON oex.order_no = om.order_no WHERE 1=1 AND om.is_deleted = 0
AND om.status = #{params.status}
AND om.user_id = #{params.userId}
GROUP BY om.sys_id,ur.relative_name

SELECT om.sys_id sysId, om.order_no orderNo, om.order_time orderTime, epm.package_name packageName, epm.subtitle, epm.content, ea.service_name serviceName, epa.periods, ea.cates, om.amount, em.name emName, oee.exam_master_item examMasterItem, ub.full_name fullName, om.total_qty totalQty, ur.relative_name customerName, ur.gender, ur.id_card_no idCardNo, ur.cellphone_no cellphoneNo, ur.remark, ub1.full_name fullName1, ub1.gender gender1, ub1.id_card_no idCardNo1, ub1.cellphone_no cellphoneNo1, om.status, om.user_channel userChannel, (SELECT SUM(ed.sys_id) FROM exam_detail ed WHERE ed.sys_id = epd.exam_detail_id ) itemNameCount FROM order_main om LEFT JOIN exam_package_main epm ON epm.sys_id = om.reference_no LEFT JOIN exam_package_append epa ON epm.sys_id = epa.package_main_id LEFT JOIN exam_append ea ON ea.sys_id = epa.append_id LEFT JOIN exam_package_detail epd ON epd.exam_main_id = epm.sys_id LEFT JOIN exam_master em ON em.sys_id = epd.exam_master_id LEFT JOIN order_extension_exam oee ON oee.order_no = om.order_no LEFT JOIN user_basic ub ON ub.user_id = om.user_id LEFT JOIN order_customer oc ON oc.order_no = om.order_no LEFT JOIN user_relatives ur ON ur.relative_id = oc.relative_id LEFT JOIN user_basic ub1 ON ub.user_id = oc.relative_id WHERE 1=1 AND om.is_deleted = 0
AND om.sys_id = #{params.sysId}
GROUP BY em.name,ea.service_name,oee.exam_master_item,ur.relative_name

SELECT epm.package_name, om.order_no, oa.creation_time, om.reference_no, om.hr_oppointment_id, om.order_cates FROM order_main om LEFT JOIN exam_package_main epm on epm.sys_id = om.reference_no LEFT JOIN order_appointment oa on om.order_no = oa.order_no WHERE (om.order_cates = 3 or om.order_cates = 1) and om.user_id = #{params.userId} and om.is_deleted = 0
AND oa.order_no = #{params.orderNo}
ORDER BY oa.creation_time DESC LIMIT 1
